import React, { useRef, useState } from "react";

const ItemRender = (props) => {
  const [changeType, setChangeType] = useState(false);
  const [border, setBorder] = useState(false);
  const [line, setLine] = useState(false);
  const handleLine = () => {
    if(changeType === false){
      setLine(!line);
    }  
  };

  const [dataNew, setDataNew] = useState(props.item);
  const inputNew = useRef();
  const handleDataNew = () => {
    setDataNew(inputNew.current.value);
  };

  const editItem = () => {
    inputNew.current.focus();
    setChangeType(true);
    setBorder(true);
    setLine(false);
  };

  // const delItem = (index) => {
  //   const newData = [...props.data];
  //   newData.splice(index, 1);
  //   console.log("trước :" + props.data);
  //   console.log("index :" + index);
  //   props.setData(newData);
  //   console.log("sau :" + newData);
  // };

  const resetPropertis = () => {
    setBorder(false);
    setChangeType(false);
  };

  const clickEnter = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      resetPropertis();
    }
  };

  return (
    <>
      <input
        ref={inputNew}
        onClick={handleLine}
        onChange={handleDataNew}
        onKeyDown={clickEnter}
        className={`Item ${border ? "border-input" : ""} ${line ? "text-line-through" : ""}`}
        type={`${changeType ? "input" : "button"}`}
        autoComplete="off"
        defaultValue={dataNew}
        onBlur={resetPropertis}
      />
      <div className="btn-icon-list">
        <i
          className="fas fa-edit btn-icon-edit"
          onClick={() => editItem(props.index)}
        />
        <i
          className="fas fa-trash btn-icon-remove"
          // onClick={() => delItem(props.index)}
          onClick={() => props.handleDelete(props.index)}
        />
      </div>
    </>
  );
};

export default ItemRender;
