import React, { createRef } from "react";
import "./index.css";
class EditableInput extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isEditing: false,
      done: false,
      inputField: this.props.label,
    };
    this.inputRef = createRef();
  }
  static defaultProps = {
    label: "Do the dishes",
    id: null,
  };
  handleOnchange = (e) => {
    this.setState({
      inputField: e.target.value,
    });
  };
  handleEditOnclick = () => {
    this.setState({
      isEditing: !this.state.isEditing,
    });
  };
  handleDeleteClick = () => {
    this.props.deleteItem(this.props.label);
  };
  render() {
    const inputForm = (
      <form>
        <input
          ref={this.inputRef}
          defaultValue={this.state.inputField}
          onChange={this.handleOnchange}
          type="text"
        />
      </form>
    );
    const labelFiled = (
      <p
        className={this.state.done ? "done" : ""}
        onClick={() => {
          this.setState({ done: !this.state.done });
        }}
      >
        {this.state.inputField}
      </p>
    );

    return (
      <div className="editableInput">
        <div className="editableInput-container">
          {this.state.isEditing ? inputForm : labelFiled}
          <div className="editableInput-edit" onClick={this.handleEditOnclick}>
            <i className="fas fa-edit"></i>
          </div>
          <div
            className="editableInput-delete"
            onClick={this.handleDeleteClick}
          >
            <i className="fas fa-trash-alt"></i>
          </div>
        </div>
      </div>
    );
  }
}
export default EditableInput;
