import React, { useRef, useState } from "react";
import RenderItem from "./components/RenderItem";
import "./App.css";
import "./index.css";
const App = () => {
  const [data, setData] = useState(["Demo_1", "Demo_2", "Demo_3"]);

  const inputRef = useRef();
  const handleDelete = (index) => {
    const newData = [...data];
    console.log(data);
    newData.splice(index, 1);
    setData(newData);
    console.log(data);
  };
  const addItem = () => {
    const txt = inputRef.current.value;
    if (txt !== "") {
      setData([...data, txt]);
      inputRef.current.value = "";
    }
  };

  const clickEnter = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      addItem();
    }
  };

  return (
    <>
      <section className="container">
        <div className="title-header">
          <h1>My to-dos</h1>
        </div>
        <div className="form-control">
          <form action="#" method="#">
            <input
              ref={inputRef}
              type="text"
              placeholder="Create a new to-do..."
              autoComplete="off"
              onBlur={addItem}
              onKeyDown={clickEnter}
            />
          </form>
        </div>
        <div className="list-group">
          <ul>
            {data.map((item, index) => (
              <li>
                <RenderItem
                  index={index}
                  item={item}
                  data={data}
                  setData={setData}
                  handleDelete={handleDelete}
                />
              </li>
            ))}
          </ul>
        </div>
      </section>
    </>
  );
};

export default App;
