import { useState, useRef } from "react";
import EditableInput from "./components/EditableInput/";

function App() {
  const [todos, setTodos] = useState([]);
  const input = useRef();
  const onSubmit = (e) => {
    e.preventDefault();
    const updatedTodos = [...todos, { title: input.current.value }];
    setTodos(updatedTodos);
  };
  const renderList = () => {
    todos.map((item) => {
      return <EditableInput label={item.title} />;
    });
  };
  return (
    <div className="app">
      <div className="app-container">
        <h2>Todo Apps</h2>
        <form onSubmit={onSubmit}>
          <input ref={input} type="text" />
        </form>
        <EditableInput label="Hoho" />
      </div>
    </div>
  );
}

export default App;
