import React, { useState, createRef } from "react";
import "./index.css";
class EditableInput extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isEditing: false,
      done: false,
      inputField: props.label,
    };
  }
  static defaultProps = {
    label: "Do the dishes",
  };

  render() {
    const input = createRef();
    const handleOnchange = (e) => {
      this.setState({
        inputField: e.target.value,
      });
    };
    const handleEditOnclick = () => {
      this.setState({
        isEditing: !this.isEditing,
      });
    };
    const inputForm = (
      <form>
        <input
          ref={input}
          defaultValue={this.inputField}
          onChange={handleOnchange}
          type="text"
        />
      </form>
    );
    const labelFiled = (
      <p
        className={this.done ? "done" : ""}
        onClick={() => {
          this.setState({ done: !done });
        }}
      >
        {this.inputField}
      </p>
    );

    return (
      <div className="editableInput">
        <div className="editableInput-container">
          {this.isEditing ? inputForm : labelFiled}
          <div className="editableInput-edit" onClick={handleEditOnclick}>
            <i className="fas fa-edit"></i>
          </div>
          <div className="editableInput-delete">
            <i className="fas fa-trash-alt"></i>
          </div>
        </div>
      </div>
    );
  }
}
export default EditableInput;
