import React, { useState, useRef } from "react";
import "./index.css";
function EditableInput({ label = "Hello world", deleteItem }) {
  const [isEditing, setIsEditing] = useState(false);
  const [done, setDone] = useState(false);
  const [inputField, setInputField] = useState(label);
  const handleOnchange = (e) => {
    setInputField(e.target.value);
  };
  const handleEditOnclick = () => {
    setIsEditing(!isEditing);
  };
  const handleDeleteClick = () => {
    deleteItem(label);
  };

  const inputForm = (
    <form>
      <input
        defaultValue={this.state.inputField}
        onChange={this.handleOnchange}
        type="text"
      />
    </form>
  );
  const labelFiled = (
    <p
      className={this.state.done ? "done" : ""}
      onClick={() => {
        this.setState({ done: !this.state.done });
      }}
    >
      {this.state.inputField}
    </p>
  );

  return (
    <div className="editableInput">
      <div className="editableInput-container">
        {this.state.isEditing ? inputForm : labelFiled}
        <div className="editableInput-edit" onClick={handleEditOnclick}>
          <i className="fas fa-edit"></i>
        </div>
        <div className="editableInput-delete" onClick={handleDeleteClick}>
          <i className="fas fa-trash-alt"></i>
        </div>
      </div>
    </div>
  );
}
export default EditableInput;
