import { useState } from "react";
import EditableInput from "./components/EditableInput/";

function App() {
  const [todos, setTodos] = useState([]);
  const onSubmit = (e) => {
    e.preventDefault();
  };
  const onChange = (e) => {
    todos = [...todos, { title: e.target.value }];
    setTodos(todos);
  };
  useEffect(() => {
    return (
      <div className="app">
        <div className="app-container">
          <h2>Todo Apps</h2>
          <form onSubmit={onSubmit}>
            <input onChange={onChange} type="text" />
          </form>
          <EditableInput />
          <EditableInput label="Hello world my name is" />
        </div>
      </div>
    );
  }, [todos]);
}

export default App;
