import { useState, useRef } from "react";
import EditableInput from "./components/EditableInput/";

function App() {
  const [todos, setTodos] = useState([]);
  const input = useRef();
  const onSubmit = (e) => {
    e.preventDefault();
    const updatedTodos = [
      ...todos,
      { title: input.current.value, id: todos.length },
    ];
    setTodos(updatedTodos);
  };
  const deleteItem = (id) => {
    setTodos(todos.splice(id, 1));
  };
  const renderList = () =>
    todos.map((item, index) => {
      return (
        <EditableInput
          deleteItem={deleteItem}
          key={index}
          id={item.id}
          label={item.title}
        />
      );
    });

  return (
    <div className="app">
      <div className="app-container">
        <h2>Todo Apps</h2>
        <form onSubmit={onSubmit}>
          <input ref={input} type="text" />
        </form>
        {console.log(todos)}
        {renderList()}
      </div>
    </div>
  );
}

export default App;
