import React, { useState, createRef } from "react";
import "./index.css";
class EditableInput extends React.Component {
  static defaultProps = {
    label: "Do the dishes",
  };

  render() {
    const [isEditing, setIsEditing] = useState(false);
    const [done, setDone] = useState(false);
    const [inputField, setInputField] = useState(label);
    const input = createRef();
    const handleOnchange = (e) => {
      setInputField(e.target.value);
    };
    const handleEditOnclick = () => {
      setIsEditing(!isEditing);
      console.log(input);
    };
    const inputForm = (
      <form>
        <input
          ref={input}
          defaultValue={inputField}
          onChange={handleOnchange}
          type="text"
        />
      </form>
    );
    const labelFiled = (
      <p
        className={done ? "done" : ""}
        onClick={() => {
          setDone(!done);
        }}
      >
        {inputField}
      </p>
    );

    return (
      <div className="editableInput">
        <div className="editableInput-container">
          {isEditing ? inputForm : labelFiled}
          <div className="editableInput-edit" onClick={handleEditOnclick}>
            <i className="fas fa-edit"></i>
          </div>
          <div className="editableInput-delete">
            <i className="fas fa-trash-alt"></i>
          </div>
        </div>
      </div>
    );
  }
}
export default EditableInput;
