import { useState, useRef } from "react";
import EditableInput from "./components/EditableInput/";

function App() {
  let [todos, setTodos] = useState([
    { title: "Do the dishes", id: 0 },
    { title: "Take out the trash", id: 1 },
    { title: "Finish doing laundry", id: 2 },
  ]);
  const input = useRef();
  const onSubmit = (e) => {
    e.preventDefault();
    const updatedTodos = [...todos, { title: input.current.value }];
    setTodos([...todos, { title: input.current.value, id: todos.length }]);
  };
  const deleteItem = (title) => {
    // console.log("deleting...", title);
    // for (let i = 0; i < todos.length; i++) {
    //   if (todos[i].title === title) {
    //     todos.splice(i, 1);
    //     break;
    //   }
    // }
    // setTodos([...todos]);
    console.log("deleting", title);
  };
  const renderList = () =>
    todos.map((item, index) => {
      return <EditableInput deleteItem={deleteItem} title={item.title} />;
    });

  return (
    <div className="app">
      <div className="app-container">
        <h2>Todo Apps</h2>
        <form onSubmit={onSubmit}>
          <input ref={input} type="text" />
        </form>
        {console.log(todos)}
        {renderList()}
      </div>
    </div>
  );
}

export default App;
