import React, { useState, useEffect } from "react";
import "./index.css";
function EditableInput({ label = "Do the dishes" }) {
  const [isEditing, setIsEditing] = useState(false);
  const [done, setDone] = useState(false);
  const inputForm = (
    <form>
      <input defaultValue={label} onChange={handleEdit} type="text" />
    </form>
  );
  const labelFiled = (
    <p
      className={done ? "done" : ""}
      onClick={() => {
        setDone(!done);
      }}
    >
      {label}
    </p>
  );
  const handleEdit = (e) => {
    console.log(e.target.value);
  };
  return (
    <div className="editableInput">
      <div className="editableInput-container">
        {isEditing ? inputForm : labelFiled}
        <div
          className="editableInput-edit"
          onClick={() => {
            setIsEditing(!isEditing);
          }}
        >
          <i className="fas fa-edit"></i>
        </div>
        <div className="editableInput-delete">
          <i className="fas fa-trash-alt"></i>
        </div>
      </div>
    </div>
  );
}

export default EditableInput;
