import { useState, useRef } from "react";
import EditableInput from "./components/EditableInput/";

function App() {
  const [todos, setTodos] = useState([]);
  const input = useRef();
  const onSubmit = (e) => {
    e.preventDefault();
    updateTodos = [...todos, { title: input.current.value }];
    setTodos(updateTodos);
    console.log(todos);
  };

  return (
    <div className="app">
      <div className="app-container">
        <h2>Todo Apps</h2>
        <form onSubmit={onSubmit}>
          <input ref={input} type="text" />
        </form>
        <EditableInput />
        <EditableInput label="Hello world my name is" />
      </div>
    </div>
  );
}

export default App;
